mapper.select:
select cr.*, d.dong_name, c.attach_no, c.original_filename, c.re_filename from ( select ca.* from ( select c.*, row_number() over(partition by craig_no order by craig_no desc )rnum from craig_attachment c)ca where rnum= 1 ) c right join craig cr on c.craig_no = cr.no left join member m on cr.writer = m.member_id left join dong d on m.dong_no = d.dong_no left join dong_range dr on d.dong_no = dr.dong_no
d.dong_name in
#{dongName}
and category_no = #{categoryNo}
and ( title like '%' || #{searchKeyword}|| '%' or content like '%' || #{searchKeyword}|| '%' )
order by cr.no desc

mapper.update:
update craig_attachment
set re_filename = #{reFilename}
where craig_no = #{craigNo}

update craig 
set hits = hits +1 
where no = #{no}

mapper.select:
select count(*) from ( select ca.* from ( select c.*, row_number() over(partition by craig_no order by craig_no desc )rnum from craig_attachment c)ca where rnum= 1 ) c right join craig cr on c.craig_no = cr.no left join member m on cr.writer = m.member_id left join dong d on m.dong_no = d.dong_no left join dong_range dr on d.dong_no = dr.dong_no
d.dong_name in
#{dongName}
and category_no = #{categoryNo}
and ( title like '%' || #{searchKeyword}|| '%' or content like '%' || #{searchKeyword}|| '%' )

select count(*) 
from  craig_wish 
where craig_no = #{no} and member_id = #{memberId}
