mapper.select:
select * from ( select l.*, (select dong_name from member m right join dong d on m.dong_no = d.dong_no where member_id = l.writer) dong_name, (select re_filename from local_attachment la where la.local_no=l.no ) re_filename from local l ) where dong_name in
#{dongName}
and category_no = #{categoryNo}
and ( title like '%' || #{searchKeyword} || '%' or content like '%' || #{searchKeyword}|| '%' )
order by reg_date desc

select count(*) from ( select l.*, ( select dong_name from member m right join dong d on m.dong_no = d.dong_no where member_id = l.writer ) dong_name from local l ) where dong_name in
#{dongName}
and category_no = #{categoryNo}

select count(*)
from local_like
where local_no = #{no} and member_id = #{memberId}

mapper.update:
update local set 
	category_no = #{categoryNo},
	title = #{title},
	content = #{content}
where
	no = #{no}

update local_comment
set
	content = #{content}
where
	comment_no = #{commentNo}
